function getQuote(){var o=getRandomNumber(quoteList.length);for(console.log(o);lastIndex==o;){var o=getRandomNumber(quoteList.length);console.log(o)}lastIndex=o,document.getElementById("quote").innerHTML=quoteList[o].quote,document.getElementById("author").innerHTML=quoteList[o].author}function getRandomNumber(o){return Math.floor(Math.random()*o)}var lastIndex=-1,quoteList=[{quote:"Anyone who considers arithmetical methods of producing random digits is, of course, in a state of sin.",author:"-John von Neumann (1951)"},{quote:"Weeks of coding can save you hours of planning.",author:"-Anonymous"}];